import mongoose from 'mongoose';
import express from 'express';
import { ApolloServer } from 'apollo-server-express';
import 'core-js/stable';
import 'regenerator-runtime/runtime';
import { typeDefs } from '../typeDefs';
import { resolvers } from '../resolvers';
import { async } from 'regenerator-runtime/runtime';
const request = require('supertest')('http://localhost:4040');

export const startServer = async () => {
  const server = new ApolloServer({
    typeDefs,
    resolvers,
    context: ({ req }) => {
      const auth = req.headers.authorization || '';
      return { auth };
    },
  });
  const app = express();

  server.applyMiddleware({ app });

  await mongoose.connect('mongodb://localhost:27017/test', {
    useNewUrlParser: true,
    useUnifiedTopology: true,
  });

  const serverExec = await app.listen({ port: 4040 }, () => {});

  return serverExec;
};

export const dropCollections = async () => {
  const collections = await mongoose.connection.db.collections();

  for (let collection of collections) {
    await collection.drop();
  }
};

export const getAuth = async () => {
  const login = await request.post('/graphql').send({
    query:
      'mutation { login (email: "test1@gmail.com", password: "1234") { token } }',
  });
  return `Bearer ${login.body.data.login.token}`;
};

import { gql } from 'apollo-server-express';

export const typeDefs = gql`
  type GeoLocation {
    type: String
    coordinates: [Float]
  }

  type Rating {
    rating: [Int]
  }

  type ProtocolRating {
    locationId: ID
    user: [User]
    ratings: [Rating]
  }

  type LocationHistory {
    location: Location
    date: String
  }

  type Location {
    geoLocation: GeoLocation
    name: String
    predictedAssistance: Int
    protocolRatings: [ProtocolRating]
  }

  type User {
    name: String
    email: String
    password: String
    locationHistory: [LocationHistory]
    ratingHistory: [ProtocolRating]
  }

  type Query {
    location(latlng: [Float]): Location
    locationRatings(location: LocationInput!): LocationRatings
  }

  input LocationInput {
    name: String
    coordinates: [Float]
  }

  type LoginOutput {
    user: User
    token: String
  }

  type StatusResponse {
    status: Boolean
    message: String
  }

  type LocationRatings {
    mainAverage: Float
    averagesCount: Int
    averages: [Float]
  }

  type Mutation {
    register(email: String!, password: String!, name: String!): Boolean
    login(email: String!, password: String!): LoginOutput
    addLocation(location: LocationInput!): Location
    noteForAssistance(location: LocationInput!): Location
    rateLocation(location: LocationInput!, ratings: [Float]!): StatusResponse
  }
`;

import { authController } from './controllers/auth.controller';
import { locationController } from './controllers/location.controller';
import { protocolRatingController } from './controllers/protocolRating.controller';

export const resolvers = {
  Query: {
    location: async (_, { latlng }, { auth }) => {
      return await authController.requireAuth(
        auth,
        locationController.getLocation,
        latlng
      );
    },
    locationRatings: async (_, { location }, { auth }) => {
      return await authController.requireAuth(
        auth,
        protocolRatingController.getLocationRatingsAverages,
        location
      );
    },
  },
  Mutation: {
    login: async (_, { email, password }, { auth }) => {
      return await authController.login(email, password, auth);
    },
    register: async (_, { email, password, name }) => {
      return await authController.register(email, password, name);
    },
    addLocation: async (_, { location }, { auth }) => {
      return await authController.requireAuth(
        auth,
        locationController.addLocation,
        location
      );
    },
    noteForAssistance: async (_, { location }, { auth }) => {
      return await authController.requireAuth(
        auth,
        locationController.noteForAssistance,
        location
      );
    },
    rateLocation: async (_, { location, ratings }, { auth }) => {
      return await authController.requireAuth(
        auth,
        protocolRatingController.rateLocation,
        { location: location, ratings: ratings }
      );
    },
  },
};

import { User } from '../models/User';
import bcrypt from 'bcrypt';
import * as config from '../.config.json';
import { JsonWebTokenError, sign, verify } from 'jsonwebtoken';
import { AuthenticationError } from 'apollo-server-errors';

const requireAuth = async (auth, f, params) => {
  if (!auth) throw new AuthenticationError('You must be logged in!');
  const token = auth.split('Bearer ')[1];
  if (!token) throw new AuthenticationError('You should provide a token!');
  const user = await verify(token, config.SECRET, (err, decoded) => {
    if (err) throw new AuthenticationError('Invalid token!');
    return decoded;
  });
  return await f(params, user);
};

const register = async (email, password, name) => {
  const user = await User.findOne({ email: email });
  if (user) {
    throw new Error('User already exists!');
  }
  const hashedPassword = await bcrypt.hash(password, 10);
  const newUser = new User({
    email,
    password: hashedPassword,
    name: name,
  });
  await newUser.save();
  return true;
};

const login = async (email, password, res) => {
  const user = await User.findOne({ email: email });
  if (!user) {
    throw new AuthenticationError('Username doesnt exist');
  }

  const valid = await bcrypt.compare(password, user.password);
  if (!valid) {
    throw new AuthenticationError('Wrong password');
  }
  const token = sign(
    {
      user,
    },
    config.SECRET,
    { expiresIn: '1d' }
  );

  return { user, token };
};

export const authController = {
  register,
  login,
  requireAuth,
};

import mongoose from "mongoose";
import { LocationModel } from "./Location";
import { ProtocolRatingModel } from "./ProtocolRating";

export const User = mongoose.model(
  "User",
  {
    name: String,
    email: String,
    password: String,
    locationHistory: [
      {
        location: LocationModel.LocationSchema,
        date: Number,
      },
    ],
    ratingHistory: [ProtocolRatingModel.ProtocolRatingSchema],
  },
  "User"
);

import mongoose, { mongo, Schema } from 'mongoose';

const pointSchema = new mongoose.Schema({
  type: {
    type: String,
    enum: ['Point'],
    required: true,
  },
  coordinates: {
    type: [Number],
    required: true,
  },
});

const LocationSchema = new Schema({
  name: String,
  geoLocation: {
    type: pointSchema,
  },
});

LocationSchema.index({ geoLocation: '2dsphere' }, { background: false });

const Location = mongoose.model('Location', LocationSchema, 'Location');

Location.createIndexes().catch((e) => console.error(e));

export const LocationModel = {
  pointSchema,
  Location,
  LocationSchema,
};

import mongoose, { mongo, Schema } from 'mongoose';
import { LocationModel } from './Location';

const ProtocolRatingSchema = new Schema({
  geoLocation: LocationModel.pointSchema,
  user: String,
  ratingId: Number,
  value: Number,
});

ProtocolRatingSchema.index({ geoLocation: '2dsphere' }, { background: false });

const ProtocolRating = mongoose.model(
  'ProtocolRating',
  ProtocolRatingSchema,
  'ProtocolRating'
);

ProtocolRating.createIndexes().catch((e) => console.error(e));

export const ProtocolRatingModel = {
  ProtocolRating,
  ProtocolRatingSchema,
};

import { LocationModel } from '../models/Location';
import { AssistanceEntries } from '../models/AssistanceEntries';

const getLocation = async (latlng) => {
  const location = await LocationModel.Location.findOne({
    geoLocation: {
      $near: {
        $geometry: {
          type: 'Point',
          coordinates: latlng,
        },
        $maxDistance: 20,
      },
    },
  });
  if (!location) {
    throw new Error('Esta localización no ha sido registrada aún.');
  }
  const predictedAssistance = await AssistanceEntries.AssistanceEntryModel.countDocuments(
    {
      location: location,
    }
  );
  return { ...location.toObject(), predictedAssistance };
};

const addLocation = async (location, user) => {
  try {
    const locationExists = await LocationModel.Location.findOne({
      geoLocation: {
        $near: {
          $geometry: {
            type: 'Point',
            coordinates: location.coordinates,
          },
          $maxDistance: 20,
        },
      },
      name: location.name,
    });
    if (locationExists) {
      throw new Error('Esta localización ya existe');
    }
    const newLocation = new LocationModel.Location({
      name: location.name,
      geoLocation: {
        type: 'Point',
        coordinates: location.coordinates,
      },
    });
    await newLocation.save();
    return { ...newLocation.toObject(), predictedAssistance: 0 };
  } catch (error) {
    throw error;
  }
};

const noteForAssistance = async (location, user) => {
  try {
    const hasEntries = await AssistanceEntries.AssistanceEntryModel.find({
      'location.geoLocation': {
        $near: {
          $geometry: {
            type: 'Point',
            coordinates: location.coordinates,
          },
          $maxDistance: 20,
        },
      },
      user: user.email,
    });
    if (hasEntries[0] != null) {
      throw new Error(
        'El usuario ya ha indicado su asistencia para esta localización.'
      );
    }
    const location_ = await LocationModel.Location.findOne({
      geoLocation: {
        $near: {
          $geometry: {
            type: 'Point',
            coordinates: location.coordinates,
          },
          $maxDistance: 20,
        },
      },
    });
    const newAssistanceEntry = new AssistanceEntries.AssistanceEntryModel({
      user: user.email,
      location: location_,
    });
    await newAssistanceEntry.save();

    const predictedAssistance = await AssistanceEntries.AssistanceEntryModel.countDocuments(
      {
        location: location_,
      }
    );
    return { ...location_.toObject(), predictedAssistance };
  } catch (error) {
    throw new Error(error);
  }
};

export const locationController = {
  getLocation,
  addLocation,
  noteForAssistance,
};

import mongoose, { mongo, Schema } from 'mongoose';
import { LocationModel } from '../models/Location';

const AssistanceEntrySchema = new mongoose.Schema({
  location: LocationModel.LocationSchema,
  user: String,
  createdAt: {
    type: Date,
    expires: 86400,
    default: Date.now,
  },
});

const AssistanceEntryModel = new mongoose.model(
  'AssistanceEntries',
  AssistanceEntrySchema,
  'AssistanceEntries'
);

export const AssistanceEntries = {
  AssistanceEntrySchema,
  AssistanceEntryModel,
};

import { ProtocolRatingModel } from '../models/ProtocolRating';

const rateLocation = async (params, user) => {
  const hasRated = await ProtocolRatingModel.ProtocolRating.find({
    user: user.user.email,
    geoLocation: {
      $near: {
        $geometry: {
          type: 'Point',
          coordinates: params.location.coordinates,
        },
        $maxDistance: 10,
      },
    },
  });
  if (hasRated[0] != null) {
    return { status: false, message: 'Ya has valorado esta ubicación' };
  }
  try {
    params.ratings.forEach(async (rating, index) => {
      const ratingEntry = new ProtocolRatingModel.ProtocolRating({
        geoLocation: {
          type: 'Point',
          coordinates: params.location.coordinates,
        },
        user: user.user.email,
        ratingId: index,
        value: rating,
      });
      await ratingEntry.save();
    });
    return { status: true, message: 'Valoración realizada correctamente' };
  } catch (mongoError) {
    return {
      status: false,
      message: 'No se ha podido establecer la conexión con la base de datos.',
    };
  }
};

const getLocationRatingsAverages = async (location, user) => {
  const averages = [];
  const averagesCount = await ProtocolRatingModel.ProtocolRating.distinct(
    'user',
    {
      geoLocation: {
        $near: {
          $geometry: {
            type: 'Point',
            coordinates: location.coordinates,
          },
          $maxDistance: 10,
        },
      },
    }
  ).exec();
  var averagesQuery = await ProtocolRatingModel.ProtocolRating.aggregate([
    {
      $geoNear: {
        distanceField: 'distance',
        spherical: true,
        near: location.coordinates,
        maxDistance: 0.00000156,
      },
    },
    {
      $group: { _id: { ratingId: '$ratingId' }, average: { $avg: '$value' } },
    },
    {
      $sort: { _id: 1 },
    },
  ]).exec();
  if (averagesQuery[0] != null) {
    averagesQuery.forEach((entry) => {
      averages.push(entry.average);
    });
  }
  return {
    mainAverage: averagesQuery[0]
      ? averages.reduce((a, b) => a + b) / averages.length
      : 0.0,
    averagesCount: averagesCount.length,
    averages,
  };
};

export const protocolRatingController = {
  rateLocation,
  getLocationRatingsAverages,
};
